
public class AreaPeriRec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l = 5;
		int b = 7;
		
		int area = l * b;
		
		int peri = 2 * (l + b);
		
		System.out.println("Area Of The Rectangle: " + area);
		
		System.out.println("Perimeter Of The Rectangle: " + peri);
		
		
		
		

	}

}
